#include <stdio.h>

int main(void){
    int age=16;
    char name[50]="kanyaphat jaturaphiphittapornchai";
    char gender ='f';
    float gap=3.13;

    printf("MY name %s \n", name); //แสดงข้อความ string
    printf("I'm %d \n", age); // เลขจำนวนเต็ฒ %d Decimal
    printf("Grade %f \n", gap); //เลขทศนิยม %f float,
    printf("Gender %c \n", gender); // %c = char
    return  0;

}
